$(document).ready((function(){const testimonialCarousel=()=>{const slider=$(".hero__carousel"),slide=$(".hero__testimonial__card"),slideNumber=slide.length,slideWidth=Math.round(slide.outerWidth()),margin=5,item=3;slider.css("width",`${slideWidth*(5*slideNumber)}px`),slide.css("margin-left","5px");let currentSlide=0;const updateSlider=()=>{const sliderTransform=slideWidth*currentSlide;slider.css("transform",`translateX(-${sliderTransform}px)`)},autoSlide=()=>{currentSlide=(currentSlide+1)%slideNumber,updateSlider()};setInterval(autoSlide,3e3)},hamburgerButton=()=>{const burger=$(".nav_burger"),navigation=$(".navigation");burger.click(()=>{navigation.toggleClass("burger-active")})},checkScreenSize=()=>{const screenWidth=$(window).width();screenWidth>=801&&$(".navigation").removeClass("burger-active")},theaterView=selector=>{$(selector).click(()=>{console.log($(this))})};$(window).resize(()=>{checkScreenSize()}),hamburgerButton(),checkScreenSize(),testimonialCarousel(),theaterView(".hero__video")}));